mapper.select:
SELECT *
  FROM music
 WHERE m_no in (select lm_m_no from like_music where lm_u_no = #{lmUNo});

SELECT *
  FROM music
 WHERE m_no = (SELECT lm_m_no
 				 FROM like_music
 				WHERE lm_u_no = #{uNo}
 				LIMIT 1);

mapper.delete:
DELETE
  FROM like_music
 WHERE lm_m_no = #{lmMNo} and lm_u_no = #{lmUNo}
